// Type declarations for static asset imports (images, svgs, etc.)
// Placing this in `src/` ensures the TS language service used by VS Code
// (when `checkJs` is enabled in `jsconfig.json`) picks these up.
declare module '*.png' {
  const value: string;
  export default value;
}

declare module '*.jpg' {
  const value: string;
  export default value;
}

declare module '*.jpeg' {
  const value: string;
  export default value;
}

declare module '*.gif' {
  const value: string;
  export default value;
}

declare module '*.svg' {
  const value: string;
  export default value;
}

declare module '*.webp' {
  const value: string;
  export default value;
}
